pas :: Args -> IO ()
pas Version = exitFailure
pas (FileInput path False eFlag tFlag oPath) = runFile 0 pProgram path
pas (FileInput path True eFlag tFlag oPath) = runCheckFile 0 pProgram path
pas StdInput = getContents >>= runParse 2 pProgram

data Args
  = Version
  | StdInput
  | FileInput
    { filePath :: String
    , fcheck :: Bool
    , feval :: Bool
    , ftrace :: Bool
    , output :: Maybe FilePath
    } deriving (Read, Show)

fileInput :: Parser Args
fileInput = do
  filePath <- strArgument
    ( help "Program file"
   <> metavar "<filepath>" )
  fcheck <- switch
    ( help "Check type of files"
   <> long "check"
   <> short 'c' )
  feval <- switch
    ( help "Evaluate program of files"
   <> long "eval"
   <> short 'e' )
  ftrace <- switch
    ( help "Print trace log"
   <> long "trace"
   <> short 't' )
  output <- optional $ strOption
    ( help "Choose output directory (default current directory)"
   <> long "output"
   <> short 'o'
   <> metavar "<path>" )
  pure FileInput {..}

stdInput :: Parser Args
stdInput =
  pure StdInput

version :: Parser Args
version = do
  infoOption "0.4.0"
    ( help "Show version"
   <> long "version"
   <> short 'v' )
  pure Version

args :: Parser Args
args = fileInput <|> stdInput <|> version